class Account:    def __init__(self, name):        # _name the under score is mean internal use only, called a weak private        self._name = name    def SetName(self, name):        self._name = name    def __str__(self):        return self._nameclass Account2:    def __init__(self, name):        # _name the under score is mean internal use only, called a weak private        self.__name = name    def SetName(self, name):        self.__name = name    # Before double underscore means internal use only and calls    # strong private    def __getmail(self):        return f'{self.__name}@gmail.com'    def __str__(self):        return f' Name:{self.__name} \n Email:{self.__getmail()}'aa = Account2('Aslan')print(aa)